package Web_cat_HW;

public class Star_printing_For {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	System.out.print("Enter number of line of * you want! ");
	int n = IO.readInt();
	for (int i =1; i<=n; i++){
		for (int g = 1; g <= i; g ++){
			System.out.print("*");
			}
	System.out.println();
		}

	}
}
